  src: url("https://cdn.jsdelivr.net/gh/pxxyyz/blog-file/fonts/FiraCode.woff2") format('woff2'), url("https://cdn.jsdelivr.net/gh/pxxyyz/blog-file/fonts/FiraCode.woff") format('woff');
}
@font-face {
  font-family: 'Edo-SZ';
