}
a.anchor {
  display: block;
  position: relative;
  top: -50px;
  visibility: hidden;
}
 @media (max-width: 767px) {
  body,
  html {
    font-size: 16px;
  }
} 
.markdown-body a {
  text-decoration: underline;
}
.markdown-body a:hover {
  color: #15967d;
}
.markdown-body h1 a::after,
.markdown-body h2 a::after,
.markdown-body h3 a::after,
.markdown-body h4 a::after,
.markdown-body h5 a::after,
.markdown-body h6 a::after,
.markdown-body span a::after {
  display: none;
}
.markdown-body mark {
  background-color: #fdf0b3;
}
.markdown-body pre {
  padding-bottom: 5px;
}
.markdown-body pre code {

  padding: 0.2rem 0rem 0.5rem 0rem;
  font-size: 0.9rem;
}
